const slides=document.querySelectorAll(".promo__item"),prev=document.querySelector(".promo__control--prev"),next=document.querySelector(".promo__control--next");let current=0;const slidesButtons=document.querySelectorAll(".promo__slider-button"),slidePromo=()=>{for(let e=0;e<slides.length;e++)slides[e].classList.remove("promo__item--current");slides[current].classList.add("promo__item--current")},toggleClasses=(e,t)=>{const r=document.querySelector(".promo__slider-button--current"),s=document.querySelector(".promo__item--current");r.classList.remove("promo__slider-button--current"),e.classList.add("promo__slider-button--current"),s.classList.remove("promo__item--current"),t.classList.add("promo__item--current")},showSlide=(e,t)=>{e.addEventListener("click",(()=>{toggleClasses(e,t)}))};if(slides.length){slidePromo(),prev.addEventListener("click",(()=>{0===current?current=slides.length-1:current--,slidePromo(),toggleClasses(slidesButtons[current],slides[current])})),next.addEventListener("click",(()=>{current===slides.length-1?current=0:current++,slidePromo(),toggleClasses(slidesButtons[current],slides[current])}));for(let e=0;e<slidesButtons.length;e++)showSlide(slidesButtons[e],slides[e])}